package com.epam.wt.dao.daoMemoryImpl;import com.epam.wt.dao.NotebookDao;import com.epam.wt.entity.Note;import com.epam.wt.logic.NoteBookLogic;import com.epam.wt.logic.NotebookAdapter;import java.time.LocalDateTime;import java.util.Collections;import java.util.List;public class NotebookDaoImpl implements NotebookDao {    private NoteBookLogic noteBookLogic = NotebookAdapter.getInstance().getNoteBook();    @Override    public List<Note> addNotes(Note note) {        noteBookLogic.addNote(note);        return Collections.emptyList();    }    @Override    public Note findNoteByIndex(int index) {        return noteBookLogic.findNoteByIndex(index);    }    @Override    public boolean removeNoteByIndex(int index) {        noteBookLogic.removeNoteByIndex(index);        return true;    }    @Override    public Note findNoteByDate(LocalDateTime localDateTime) {        noteBookLogic.findNoteByDate(localDateTime);        return null;    }    @Override    public boolean removeNoteByDate(LocalDateTime dateTime) {        noteBookLogic.removeNoteByDate(dateTime);        return true;    }    @Override    public List<Note> sortByDate() {        noteBookLogic.sortByDate();        return Collections.emptyList();    }}